// function ScriptFunction(info)
//   - info: information of web page in active tab
//   - return value: {html:<customPageHTML>, action:<actionName>, data:<dataForNextActionFunction>}
function ScriptFunction(info) {
    const execFile = require('child_process').execFile;
    const title = info.title;
    execFile('cmd',  ['/c', 'start', 'cmd', '/c', `echo Title = ${title} & pause`]);

    //execFile('cmd',  ['/c', 'start', 'powershell', '-c', 'echo xxx; Read-Host "Hit Return"']);
    //execFile('notepad.exe');

    return {action:"action1", data:info.html};  // return to browser extension
}

// function ScriptFunction(info)
//   - info: {form:<formData>, data:<dataFromPreviousScript>}
//   - return value: {html:<customPageHTML>, action:<actionName>, data:<dataForNextActionFunction>}
function ScriptFunction(info){
  const domparser = new DOMParser();
  const doc = domparser.parseFromString(info.data, "text/html");
  let t = doc.querySelector("title").innerText;
  let url = 'https://search.yahoo.com/search?p=' + t;
  window.open(url);
  return {html:"<h3>TITLE:  "+t+"</h3>"};
}

