// function ScriptFunction(info)
//   - info: {form:<formData>, data:<dataFromPreviousScript>}
//   - return value: {html:<customPageHTML>, action:<actionName>, data:<dataForNextActionFunction>}
function ScriptFunction(info) {
  const formData = info.form;  // = [["name1", "value1"], ["name2", "value2"], ...]
  const yourName = formData.find(e => e[0] === "yourname")[1];
  return {
    html:`
        	<h3>Custom page 2<h3>
        	<p>your name is ${yourName}<p>
            <form>
                	<input type='button' data-action='none' value='OK'>
            </form>`
  };
}
