nativeScript.nativeScript:
// function ScriptFunction(info)
//   - info: information about web page in active tab
//   - return value: object passed to the browser extension
function ScriptFunction(info) {
    const { JSDOM } = require("jsdom");
    const execFile = require('child_process').execFile;

    const html = info.html;
    const dom = new JSDOM(html);
    const doc = dom.window.document;
    const query = doc.querySelector("input[name='q']").value;
    execFile('cmd',  ['/c', 'start', '"title"', 'https://search.yahoo.com/search?p=' + query]);

    return {result:"success"};  // return to browser extension
}

// function ScriptFunction(info)
//   - info: information of web page in active tab
//   - return value: object passed to the browser extension
function ScriptFunction(info) {
  // user logic
}

