function ScriptFunction(info) {
    const execFile = require('child_process').execFile;
    const title = info.title;
    execFile('cmd',  ['/c', 'start', 'cmd', '/c', `echo Title = ${title} & pause`]);

    //execFile('cmd',  ['/c', 'start', 'powershell', '-c', 'echo xxx; Read-Host "Hit Return"']);
    //execFile('notepad.exe');

    return {result:"success", html:info.html};  // return to browser extension
}

